import React, { Component } from "react";
import {Link} from "react-router-dom";
import OwlCarousel from 'react-owl-carousel';
import "owl.carousel/dist/assets/owl.carousel.css";
import "owl.carousel/dist/assets/owl.theme.default.css";
import { useHistory } from 'react-router-dom';
import { Redirect } from 'react-router-dom';

class TourGuide extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
       email: null,
       password: null,
       username:null,
       phone:null,
       location:null,
       type:"Tourguide",
       redirect: false,
       isDisabled:true
       //msg:null,
       
      };
      this.submitForm = this.submitForm.bind(this);
      
  };
//////////start of regex ///////
  validateEmail(email){
    const pattern = /[a-zA-Z0-9]+[\.]?([a-zA-Z0-9]+)?[\@][a-z]{3,9}[\.][a-z]{2,5}/g;
    const result = pattern.test(email);
    if(result===true){
      this.setState({
        emailError:false,
        email:email
      })
    } else{
      this.setState({
        emailError:true
      })
    }
  }
  validatePassword(password){
    const pattern = /(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}/g;
    const result = pattern.test(password);
    if(result===true){
      this.setState({
        passwordError:false,
        password:password
      })
    } else{
      this.setState({
        passwordError:true
      })
    }
  }
  validatePhone(phone){
    const pattern = /[+]20[0-2,5]{1}[0-9]{9}$/g;
    const result = pattern.test(phone);
    if(result===true){
      this.setState({
        phoneError:false,
        phone:phone
      })
    } else{
      this.setState({
        phoneError:true
      })
    }
  }
/////end of regex///////
  ///////redirection //////////
  setRedirect = () => {
    this.setState({
      redirect: true
   
    })
   
  }
  renderRedirect = () => {
    if (this.state.redirect) {
      return <Redirect to='/registeration' />
    }
  }
    
    //////////end of redirection /////
      /**   const [email, setEmail] = useState('');
        const [password, setPassword] = useState('');
        const [msg, setMsg] = useState('');
        const history = useHistory(); */
    
        handleSubmit = (event) => {
          alert('A form was submitted: ');
          event.preventDefault();
        }
      
      
        fetchData() {
          fetch("http://localhost:8000/signup", {
              method: "POST",
              body: JSON.stringify({
                 email: this.state.email,
                 password: this.state.password,
                 username:this.state.username,
                 phone:this.state.phone,
                 location:this.state.location,
                 type:this.state.type
              }),
              headers: {
                  "Content-type": "application/json; charset=UTF-8"
              }
          })
          .then(response => response.json())
          // .then(data=>{
          //   this.setState({token: data.accessToken})
          // })
          console.table(this.state.email);
          console.table(this.state.password);
          console.table(this.state.userName);
          console.table(this.state.location);
          console.table(this.state.phone);
      
        }
      
        componentDidMount() {
      
        }
        ////////////{ Validation }///////////
        handleChange(e){
          const target = e.target;
          const value = target.type === 'checkbox' ? target.checked : target.value;
          const name = target.name;
          this.setState({
            [name]: value
          });
          if(e.target.name==='username'){
            if(e.target.value==='' || e.target.value===null ){
              this.setState({
                usernameError:true
              })
            } else {
              this.setState({
                usernameError:false,     
                userName:e.target.value
              })
            }
          }
          if(e.target.name==='location'){
            if(e.target.value==='' || e.target.value===null){
              this.setState({
                locationError:true
              })
            } else {
              this.setState({
                locationError:false,
               location:e.target.value
              })
            }
          }
          if(e.target.name==='email'){
           this.validateEmail(e.target.value);
          }
          if(e.target.name==='password'){
            this.validatePassword(e.target.value);
           }
           if(e.target.name==='phone'){
            this.validatePhone(e.target.value);
           }
           
         if(this.state.usernameError===false && this.state.locationError===false && 
          this.state.emailError===false && this.state.passwordError===false && this.state.phoneError==false){
            this.setState({
              isDisabled:false
            })
         }
        }
        submitForm(e){
          e.preventDefault();
          const data = {
           userName: this.state.userName,
          location: this.state.location,
           email: this.state.email,
           password: this.state.password,
           phone: this.state.phone,
           
          }
          // sendFormData(data).then(res=>{
          //   if(res.status===200){
          //     alert(res.data);
          //     this.props.history.push('/');
          //   }else{
        
          //   } 
          // });
         }

    render() {
     
      
        return (
            <>
                {/* =============== SignIn =============== */}
                <form className="form" id="signup-form"  onSubmit={this.setRedirect}>
                      <img src="data:image/png;base64," alt=""></img>
                <h3>Sign Up Now </h3>
                <label>it's quick and easy </label>
               

               <div className="form-group">
                <div className="form-label-group">
                  <input type="text2" id="username" name="username"required className="form-control" placeholder="Enter username" onChange={(e)=>{this.handleChange(e)}} />
                  <label htmlFor="firstname"></label>
                  {this.state.usernameError ? <span style={{color: "orangered"}}>Please Enter some value</span> : ''} 
                </div>
              </div>
              

                <div className="form-group">
                <div className="form-label-group">
                  <input type="email2" id="email" name="email" required className="form-control" placeholder="Enter your email" onChange={(e)=>{this.handleChange(e)}} />
                  <label htmlFor="email"></label>
                  {this.state.emailError ? <span style={{color: "orangered"}}>Please Enter valid email address : example@mail.com </span> : ''}
                </div>
              </div> 
             

                   <div className="form-group">
                <div className="form-label-group">
                  <input type="password" id="password" name="password" required className="form-control" placeholder="Password" onChange={(e)=>{this.handleChange(e)}} />
                  <label htmlFor="password"></label>
                  {this.state.passwordError ? <span style={{color: "orangered"}}>Must contain at least one number and one uppercase and lowercase letter, and at least 8 or more characters </span> : ''}
                </div>
              </div>

              <div className="form-group">
                <div className="form-label-group">
                  <input type="text2" id="phone" name="phone"required className="form-control" placeholder="Phone Number" onChange={(e)=>{this.handleChange(e)}} />
                  <label htmlFor="phone"></label>
                  {this.state.phoneError ? <span style={{color: "orangered"}}>Enter Valid Phone Number ex :+20-XX-XXX-XXXX </span> : ''}
                </div>
              </div>
            
                  <div className="form-group">
                <div className="form-label-group">
                  <input type="location" id="location" name="location" required className="form-control" placeholder="Location" onChange={(e)=>{this.handleChange(e)}} />
                  <label htmlFor="location"></label>
                  {this.state.locationError ? <span style={{color: "orangered"}}>Please Enter some value</span> : ''}
                </div>
              </div>
                <div className="button-container">
                {this.renderRedirect()}
                 <input type="submit" value="Sign up" onClick={() => this.fetchData()} />
                </div>

                <div class="textInHorizontalLine">
                    <span>Already a member?</span>
                </div>
                 <div>
                 <a class="coreRegLinkHighlight" href="registeration">Sign in</a> 
                  <span> using your EgyBook account.</span>
                       
                 </div>
            </form>




         
            </>
        
        );
      
    } 
}


 
export default TourGuide;